import React from 'react'
import { Typography, Stack, Button } from '@mui/material'

import BodyPartImage from '../assets/icons/body-part.png'
import TargetImage from '../assets/icons/target.png'
import EquipmentImage from '../assets/icons/equipment.png'
import Loader from './Loader'

const Detail = ({ ExerciseDetail }) => {
    const { bodyPart, gifUrl, name, target, equipment } = ExerciseDetail;
    // console.log(gifUrl)


    const extraDetail = [
        {
            icon: BodyPartImage,
            name: bodyPart,
        },
        {
            icon: TargetImage,
            name: target,
        },
        {
            icon: EquipmentImage,
            name: equipment,
        }
    ];
    return (
        <Stack gap="30px" sx={{ flexDirection: { lg: 'col' }, p: '20px'}}>
            <Stack gap="60px" sx={{ flexDirection: { lg: 'row' }, p: '20px', alignItems: 'center' }}>
                <img src={gifUrl} alt={name} loading="lazy" className="detail-image" />

                <Stack sx={{ gap: { lg: '35px', xs: '20px' } }}>
                    <Typography sx={{ fontSize: { lg: '64px', xs: '30px' } }} fontWeight={700} textTransform="capitalize">
                        {name}
                    </Typography>

                    <Typography sx={{ fontSize: { lg: '24px', xs: '18px' } }} color="#4F4C4C" >
                        Exercises Keep you  strong. {' '}
                        <span style={{ textTransform: 'capitalize' }}>{name}</span> bup is one
                        of the best <br /> exercises to target your {target}. It will help you improve your{' '}
                        <br /> mood and gain energy.
                    </Typography>

                    {
                        extraDetail.map((item) => (

                            <Stack key={item.name} direction='row' gap='24px' alignItems='center'>
                                <Button sx={{ background: '#FFF2DB', borderRadius: '50%', width: '100px', height: '100px' }}>
                                    <img src={item.icon} alt={item.name} style={{ width: '50px', height: '50px' }} />
                                </Button>
                                <Typography textTransform="capitalize" sx={{ fontSize: { lg: '30px', xs: '20px' } }}>
                                    {item.name}
                                </Typography>
                            </Stack>
                        ))
                    }
                </Stack>
            </Stack>
            {/* <Typography sx={{ fontSize: { lg: '46px', xs: '28px' } }} fontWeight={700} textTransform="capitalize" alignItems='flex-start'>
                Workout Procedures
            </Typography>
                   { console.log(ExerciseDetail.instructions)}
            <Typography sx={{ fontSize: { lg: '24px', xs: '18px' } }} color="#4F4C4C" >
                {ExerciseDetail.instructions.map((instruction, index) => (
                    <div key={index}>{index+1}. {instruction}</div>
                ))} 
            </Typography> */}
        </Stack>
    )
}

export default Detail
